import React, { useState, useEffect } from "react";
import { Button, Container, Form, Row, Col, Image, Table } from "react-bootstrap";
import axios from "axios";
import catarrow from "../../assets/catarrow1.png";

const Addsubcategory = () => {
  const [subcategoryName, setSubcategoryName] = useState("");
  const [category, setCategory] = useState(""); 
  const [categories, setCategories] = useState([]); 
  const [subcategories, setSubcategories] = useState([]); // To store subcategories
  const [image, setImage] = useState(null); 

  // Fetch categories from backend
  useEffect(() => {
    const fetchCategories = async () => {
      try {
        const response = await axios.get("http://localhost:8000/api/categories"); 
        setCategories(response.data || []);
      } catch (error) {
        console.error("Error fetching categories:", error);
      }
    };

    const fetchSubcategories = async () => {
      try {
        const response = await axios.get("http://localhost:8000/api/subcategories");
        setSubcategories(response.data || []);
      } catch (error) {
        console.error("Error fetching subcategories:", error);
      }
    };

    fetchCategories();
    fetchSubcategories();
  }, []);

  // Handle file upload
  const handleImageUpload = (file) => {
    setImage(file);
  };

  // Save the new subcategory
  const handleSave = async () => {
    if (!subcategoryName || !category || !image) {
      alert("Please fill in all fields.");
      return;
    }

    // Prepare FormData for the backend
    const formData = new FormData();
    formData.append("subcategoryName", subcategoryName);
    formData.append("category", category); // Include category ID
    formData.append("image", image); // Include the uploaded image

    try {
      const response = await axios.post("http://localhost:8000/api/subcategories", formData, {
        headers: { "Content-Type": "multipart/form-data" },
      });
      alert("Subcategory added successfully");
    } catch (error) {
      console.error("Error adding subcategory:", error);
      alert("Failed to add subcategory");
    }
  };

  return (
    <>
      <Container>
        <Row>
          <Col lg={12}>
            <div style={{ display: "flex", flexDirection: "row", gap: "20px" }}>
              <Image style={{ width: "24px", height: "24px", marginTop: "4px" }} src={catarrow} />
              <h3>Add Sub Category</h3>
            </div>

            <div style={{ display: "flex", flexDirection: "row", justifyContent: "flex-start", gap: "40px" }}>
              {/* Category Dropdown */}
              <Form.Group className="mb-3" controlId="categorySelect" style={{ width: "350px" }}>
                <Form.Label style={{ fontSize: "20px", fontFamily: "Poppins" }}>
                  Category
                </Form.Label>
                <Form.Select
                  style={{
                    width: "100%",
                    height: "61px",
                    border: "1px solid #9F9F9F",
                    borderRadius: "10px",
                    paddingLeft: "10px",
                    fontFamily: "Poppins",
                    fontSize: "16px",
                  }}
                  value={category}
                  onChange={(e) => setCategory(e.target.value)}
                >
                  <option value="">Select a category</option>
                  {categories.map((cat) => (
                    <option key={cat._id} value={cat._id}>
                      {cat.categoryName}
                    </option>
                  ))}
                </Form.Select>
              </Form.Group>
            </div>
          </Col>
        </Row>

        {/* Subcategories List */}
        <Row>
          <Col lg={12}>
            <h3 style={{ marginTop: "30px" }}>Subcategories</h3>
            <Table striped bordered hover>
              <thead>
                <tr>
                  <th>#</th>
                  <th>Subcategory Name</th>
                  <th>Category</th>
                </tr>
              </thead>
              <tbody>
                {subcategories.map((subcat, index) => (
                  <tr key={subcat._id}>
                    <td>{index + 1}</td>
                    <td>{subcat.subcategoryName}</td>
                    <td>{categories.find((cat) => cat._id === subcat.category)?.categoryName || "Unknown"}</td>
                  </tr>
                ))}
              </tbody>
            </Table>
          </Col>
        </Row>

        {/* Action Buttons */}
        <div style={{ marginTop: "30px", float: "right", display: "flex", justifyContent: "center", gap: "31px" }}>
          <Button
            style={{
              borderRadius: "25px",
              width: "159px",
              height: "52px",
              backgroundColor: "#FFFFFF",
              border: "1px solid #9F9F9F",
            }}
            onClick={() => alert("Cancel")}
          >
            Cancel
          </Button>
          <Button
            style={{
              borderRadius: "25px",
              width: "159px",
              height: "52px",
              backgroundColor: "#662671",
              border: "1px solid #9F9F9F",
              color: "white",
            }}
            onClick={handleSave}
          >
            Save
          </Button>
        </div>
      </Container>
    </>
  );
};

export default Addsubcategory;
