import React, { useState, useEffect } from "react";
import { useTable } from "react-table";
import { Image } from "react-bootstrap";
import editicon from "../../assets/editicon.png";
import deleteicon from "../../assets/deleteicon.png";
import filterarrow from "../../assets/filterarrow.png";
import { Card, Modal } from "react-bootstrap";
import warningicon from "../../assets/warningicon.png";
import { Link } from "react-router";
import axios from "axios"; // Import Axios for API requests
import { useParams } from "react-router";

const CategoryTable = () => {
  const [show, setShow] = useState(false);
  const [categories, setCategories] = useState([]); // State to hold categories data
  const [deleteId, setDeleteId] = useState(null);
const {id}  = useParams();
  const handleShow = (id) => {
    setDeleteId(id); // Set the category id to delete
    setShow(true);
  };
  const handleClose = () => setShow(false);





  const handleDelete = async () => {
    try {
      // Send DELETE request to backend
      const response = await axios.delete(`http://localhost:8000/api/categories/${deleteId}`);;
      console.log(response.data);
      alert("Category deleted successfully");
      setShow(false);
      // Refresh the categories list
      setCategories(categories.filter(category => category.id !== deleteId));
    } catch (error) {
      console.error("Error deleting category:", error);
      alert("Failed to delete category");
    }
  };




  // Fetch categories from the backend API
  useEffect(() => {
    const fetchCategories = async () => {
      try {
        const response = await axios.get("http://localhost:8000/api/categories"); // Replace with your API endpoint
        setCategories(response.data)
        console.log(response.data)
        ; // Update the categories state with API response
      } catch (error) {
        console.error("Error fetching categories:", error);
      }
    };

    fetchCategories();
  }, []);

  // Column definitions
  const columns = React.useMemo(
    () => [
      { Header: "Id", accessor: "id" },
      { Header: "Category Name", accessor: "categoryName" },
      {
        Header: "Image",
        accessor: "image",
        Cell: ({ value }) => (
          <img
            src={`http://localhost:8000/uploads/${value}`} // Make sure to adjust this if needed
            alt="Category"
            style={{
              width: "50px",
              height: "50px",
              objectFit: "cover",
              borderRadius: "8px",
            }}
          />
        ),
      },

      {
        Header: "Status",
        accessor: "status",
        Cell: ({ value }) => (
          <span
            style={{
              padding: "5px 10px",
              borderRadius: "12px",
              color: value ? "green" : "red",
            }}
          >
            {value ? "Active" : "Inactive"}
          </span>
        ),
      },
      {
        Header: "Action",
        accessor: "action",
        Cell: ({ row }) => (
          <div style={styles.actionContainer}>
            <Link to={`/category/edit/${row.original.id}`}> {/* Pass the category id in the URL */}
              <Image
                src={editicon}
                onClick={() => handleEdit(row.original)}
              />
            </Link>
            <Image
              src={deleteicon}
              onClick={() => handleShow(row.original.id)}
            />
          </div>
        ),
      },
    ],
    []
  );

  // React Table hook
  const {
    getTableProps,
    getTableBodyProps,
    headerGroups,
    rows,
    prepareRow,
  } = useTable({ columns, data: categories });

  const handleEdit = (row) => {
    console.log("Edit clicked:", row);
  };


  return (
    <div style={styles.tableContainer}>
      <table {...getTableProps()} style={styles.table}>
        <thead>
          {headerGroups.map((headerGroup) => (
            <tr {...headerGroup.getHeaderGroupProps()} style={styles.headerRow}>
              {headerGroup.headers.map((column) => (
                <th {...column.getHeaderProps()} style={styles.headerCell}>
                  {column.render("Header")}
                  <Image
                    src={filterarrow}
                    alt="Filter"
                    style={styles.filterIcon}
                  />
                </th>
              ))}
            </tr>
          ))}
        </thead>
        <tbody {...getTableBodyProps()}>
          {rows.map((row) => {
            prepareRow(row);
            return (
              <tr {...row.getRowProps()} style={styles.row}>
                {row.cells.map((cell) => (
                  <td {...cell.getCellProps()} style={styles.cell}>
                    {cell.render("Cell")}
                  </td>
                ))}
              </tr>
            );
          })}
        </tbody>
      </table>
      <Modal show={show} onHide={handleClose} centered>
        <Modal.Body>
          <Card style={{ border: "none" }}>
            <Card.Body>
              <div
                style={{
                  display: "flex",
                  justifyContent: "center",
                  flexDirection: "row",
                  alignItems: "center",
                  gap: "9px",
                  marginTop: "-10px",
                  position: "relative",
                }}
              >
                <Image
                  style={{ width: "45px", height: "45px" }}
                  src={warningicon}
                ></Image>
                <Card.Title
                  style={{
                    marginTop: "20px",
                    fontFamily: "'Poppins'",
                    fontStyle: "normal",
                    fontWeight: 600,
                    fontSize: "22px",
                    lineHeight: "48px",
                    color: "#000000",
                  }}
                >
                  Delete
                </Card.Title>
              </div>
              <Card.Text
                style={{
                  textAlign: "center",
                  fontFamily: "'Poppins'",
                  fontStyle: "normal",
                  fontWeight: 400,
                  fontSize: "20px",
                  lineHeight: "36px",
                  color: "#8F8F8F",
                }}
              >
                Are you sure you want to delete?
              </Card.Text>
              <div
                style={{
                  display: "flex",
                  justifyContent: "space-evenly",
                  alignItems: "center",
                  flexDirection: "row",
                }}
              >
                <button
                  style={{
                    borderRadius: "25px",
                    width: "159px",
                    height: "52px",
                    backgroundColor: "#FFFFFF",
                    border: "1px solid #9F9F9F",
                  }}
                  variant="outlined"
                  onClick={handleClose}
                >
                  Close
                </button>
                <button
                  style={{
                    borderRadius: "25px",
                    width: "159px",
                    height: "52px",
                    backgroundColor: "#662671",
                    border: "1px solid #9F9F9F",
                    color: "white",
                  }}
                  variant="outlined"
                  onClick={handleDelete}
                >
                  Confirm
                </button>
              </div>
            </Card.Body>
          </Card>
        </Modal.Body>
      </Modal>
    </div>
  );
};
const styles = {
  tableContainer: {
    width: "100%",
    padding: "20px",
  },
  table: {
    width: "100%",
    borderCollapse: "separate", // Ensure separation for spacing
    borderSpacing: "0 12px", // Adds spacing between rows (12px vertical spacing)
  },
  headerRow: {
    backgroundColor: "#FFF8B7", // Set header color to #FFF8B7
  },
  headerCell: {
    padding: "10px",
    fontFamily: "'Poppins'",
    fontStyle: "normal",
    fontWeight: 400,
    fontSize: "20px",
    lineHeight: "30px",
    color: "#000000", // Font color for header
    borderBottom: "2px solid #ddd",
    border: 'none',
    height: '60px'
  },
  row: {
    height: "60px",
    backgroundColor: "#F2F2F2",
    // Set row color to #F2F2F2
  },
  cell: {
    padding: "10px",
    fontFamily: "'Poppins'",
    fontStyle: "normal",
    fontWeight: 400,
    fontSize: "20px",
    lineHeight: "30px",
    color: "#000000", // Font color for row cells
    borderBottom: "1px solid #ddd",
    border: 'none',

  },
  actionContainer: {
    display: "flex",
    justifyContent: "space-around",
    alignItems: "center",
    gap: "10px",
  },
  filterIcon: {
    width: "13px", // Set width of the filter arrow
    height: "24px",
    position: 'relative',
    left: '10px' // Set height of the filter arrow
  },
};

export default CategoryTable;
