import React from "react";
import Container from 'react-bootstrap/Container';
import Row from 'react-bootstrap/Row';
import Col from 'react-bootstrap/Col';
import Image from 'react-bootstrap/Image';
import heroimage from '../../assets/heroimage.png';
import Button from 'react-bootstrap/Button';
import Card from 'react-bootstrap/Card';
import './login.css';
import logo from '../../assets/logo.png'
import Form from 'react-bootstrap/Form';
import passImage from '../../assets/pass.png';
import { useState, useEffect } from "react";
import { useNavigate } from 'react-router-dom';
import { useAuth } from "../../context/Authcontext";
import Modal from 'react-bootstrap/Modal';
import axios from "axios";
const forgotPassword = () => {

    // useEffect(() => {
    //     if (togglePass === 'text') {
    //         setTimeout(() => {
    //             setTogglePass('password')
    //         }, 2000)
    //     }
    // })




    const [email, setEmail] = useState('');
    const [resetemail, setResetEmail] = useState('');
    const [password, setPassword] = useState('');
    const { login, user, EmailPass } = useAuth(); // Use context

    const navigate = useNavigate();

    const [show, setShow] = useState(false);

    const handleClose = () => setShow(false);
    const handleShow = () => setShow(true);

    useEffect(() => {
        if (user) {
            navigate('/home');  // Replace '/dashboard' with your protected route
        }
    }, [user, navigate]);

    const handleLogin = async (e) => {
        e.preventDefault();
        await login(email, password);
        alert('Login Success');
        navigate('/home')


    };

    const handleResetLink = async (e) => {
        e.preventDefault();
        setResetEmail(e.target.value);



    }

    const handleResetSubmit = async () => {
        const res = await EmailPass(resetemail);
        alert(res);
    }
    return (
        <>
            <Container fluid>
                <Row>
                    <Col sm={12}>
                        <div className="login_container">
                            <Image src={heroimage} fluid />
                        </div>
                        <Card className="custom_card" style={{
                            width: "660px",
                            height: "802px",
                            left: "103px",
                            top: "147px",
                            position: 'absolute'
                        }}>
                            <Form.Label style={{
                                width: "153px",
                                height: "30px",
                                fontFamily: "Poppins",
                                fontStyle: "normal",
                                fontWeight: 300,
                                fontSize: "15px",
                                lineHeight: "30px",
                                color: "rgb(157, 157, 157)",
                                flex: "0 0 auto",
                                position: "relative",


                                zIndex: 1,

                                left: "18px",

                            }}>Email address</Form.Label>
                            <Form.Control onChange={handleResetLink} style={{
                                position: 'relative',
                                width: "100%",
                                height: "61px",

                                border: "1px solid #9F9F9F",
                                borderRadius: "10px"
                            }} type="email" />
                        </Card>

                    </Col>
                </Row>
            </Container>
        </>
    )
}

export default forgotPassword
