import heroimage from '../../assets/heroimage.png';
import Button from 'react-bootstrap/Button';
import Card from 'react-bootstrap/Card';
import './login.css';
import logo from '../../assets/logo.png'
import Form from 'react-bootstrap/Form';
import passImage from '../../assets/pass.png';
import { useState, useEffect } from "react";
import { useNavigate, useParams } from 'react-router-dom';
import { useAuth } from "../../context/Authcontext";
import Modal from 'react-bootstrap/Modal';
import axios from "axios";
const ForgotPassword = () => {
   const token = useParams();
   const [newPassword,setNewPassword] = useState('')
   const handleNewPass = async (e)=>{
    e.preventDefault();
    setNewPassword(e.target.value)
   }

   const handleNewPassword = async(e)=>{
    try{
        const res = axios.post(`http://localhost:8000/api/user//reset-password/${token}`,{newPassword});
alert('Password Reset Successful')
    }catch(err){
        alert('Password Reset Failed')
       }
   }
    return (
        <>
            <Container fluid>
                <Row>
                    <Col sm={6}>
                            <Form.Label onChange={handleNewPass} style={{
                                width: "153px",
                                height: "30px",
                                fontFamily: "Poppins",
                                fontStyle: "normal",
                                fontWeight: 300,
                                fontSize: "15px",
                                lineHeight: "30px",
                                color: "rgb(157, 157, 157)",
                                flex: "0 0 auto",
                                position: "relative",
                                zIndex: 1,

                                left: "18px",


                            }}>Enter New Password</Form.Label>
                            <Form.Control style={{
                                position: 'relative',
                                width: "100%",
                                height: "61px",

                                border: "1px solid #9F9F9F",
                                borderRadius: "10px"
                            }} type="email" />
                            <br/>


                            <Button onClick={handleNewPassword}>Submit</Button>


                    </Col>
                </Row>
            </Container>
        </>
    )
}

export default ForgotPassword
