import React from "react";
import { Button, Container } from "react-bootstrap";

import Row from "react-bootstrap/Row";
import Col from "react-bootstrap/Col";
import { Image } from "react-bootstrap";

import catarrow from "../../assets/catarrow1.png";
import Form from "react-bootstrap/Form";

const AddProduct = () => {
    useEffect(() => {
        const fetchCategories = async () => {
          try {
            const response = await axios.get("http://localhost:8000/api/categories"); 
            setCategories(response.data || []);
            if (response.data && response.data.length > 0) {
              setCategory(response.data); 
            }
          } catch (error) {
            console.error("Error fetching categories:", error);
          }
        };

        fetchCategories();
      }, []);
    const [productName, setProductName] = useState("");
    const [image, setImage] = useState(null); // To hold the selected image

    const handleImageUpload = (file) => {
        setImage(file); // Assuming Imageuploader returns the selected image
    };

    const handleCategoryNameChange = (e) => {
        setCategoryName(e.target.value);
    };

    const handleSave = async () => {
        if (!categoryName || !image) {
          alert("Please fill all fields.");
          return;
        }

        // Prepare form data to send to the backend
        const formData = new FormData();
        formData.append("categoryName", categoryName);
        formData.append("image", image); // Pass the raw image file to the backend

        try {
          // Send POST request to the backend
          const response = await axios.post("http://localhost:8000/api/categories", formData, {
            headers: {
              "Content-Type": "multipart/form-data", // Set header for file upload
            },
          });
          console.log(response.data);
          alert("Category added successfully");
        } catch (error) {
          console.error("Error adding category:", error);
          alert("Failed to add category");
        }
      };
    return (
        <>
            <Container>
                <Row>
                    <Col lg={12}>
                        <div>
                            <div
                                style={{
                                    display: "flex",
                                    flexDirection: "row",
                                    gap: "20px",
                                }}
                            >
                                <Image
                                    style={{
                                        width: "24px",
                                        height: "24px",
                                        marginTop: "4px",
                                    }}
                                    src={catarrow}
                                ></Image>
                                <h3>Add Product</h3>
                            </div>
                            <div
                                style={{
                                    display: "flex",
                                    flexDirection: "row",
                                    justifyContent: "flex-start",
                                    gap: "40px",
                                }}
                            >
                                <Form.Group
                                    className="mb-3"
                                    controlId="exampleForm.ControlInput1"
                                >
                                    <Form.Label
                                        style={{
                                            width: "171px",
                                            height: "30px",
                                            fontFamily: "Poppins",
                                            fontStyle: "normal",
                                            fontWeight: 400,
                                            fontSize: "20px",
                                            lineHeight: "30px",
                                            color: "rgb(0 0 0)",
                                            flex: "0 0 auto",
                                            position: "relative",
                                            top: "25px",
                                            paddingLeft: "7px",
                                            zIndex: 12,
                                            background: "white",
                                            left: "18px",
                                            paddingRight: "-24px",
                                        }}
                                    >
                                        Product Name
                                    </Form.Label>
                                    <Form.Control
                                        style={{
                                            position: "relative",
                                            width: "220px",
                                            height: "61px",
                                            zIndex: 9,

                                            border: "1px solid #9F9F9F",
                                            borderRadius: "10px",
                                        }}
                                        type="text"
                                    />
                                </Form.Group>
                                <div>
                                    <Form.Group
                                        className="mb-3"
                                        controlId="statusSelect"
                                        style={{
                                            position: "relative",
                                            width: "350px",
                                        }}
                                    >
                                        <Form.Label
                                            style={{
                                                width: "134px",
                                                height: "30px",
                                                fontFamily: "Poppins",
                                                fontStyle: "normal",
                                                fontWeight: 400,
                                                fontSize: "20px",
                                                lineHeight: "30px",
                                                color: "rgb(0, 0, 0)",
                                                flex: "0 0 auto",
                                                position: "relative",
                                                top: "25px",
                                                left: "114px",
                                                zIndex: 12,
                                                background: "white"
                                            }}
                                        >
                                            SubCategory
                                        </Form.Label>
                                        <Form.Select
                                            style={{
                                                position: "relative",
                                                width: "100%",
                                                height: "61px",
                                                zIndex: 9,
                                                border: "1px solid #9F9F9F",
                                                borderRadius: "10px",
                                                paddingLeft: "10px",
                                                fontFamily: "Poppins",
                                                fontStyle: "normal",
                                                fontWeight: 400,
                                                fontSize: "16px",
                                                lineHeight: "24px",
                                                position: "relative",
                                            }}
                                        >
                                            <option value="Active">Existing Category 1</option>
                                            <option value="Inactive">Existing Category 2x</option>
                                        </Form.Select>


                                        <Form.Label
                                            style={{
                                                width: "106px",
                                                height: "30px",
                                                fontFamily: "Poppins",
                                                fontStyle: "normal",
                                                fontWeight: 400,
                                                fontSize: "20px",
                                                lineHeight: "30px",
                                                color: "rgb(0 0 0)",
                                                flex: "0 0 auto",
                                                position: "relative",
                                                top: "25px",
                                                paddingLeft: "7px",
                                                zIndex: 12,
                                                background: "white",
                                                left: "18px",
                                                paddingRight: "-24px",
                                            }}
                                        >
                                            Category
                                        </Form.Label>
                                        <Form.Control
                                            style={{
                                                position: "relative",
                                                width: "220px",
                                                height: "61px",
                                                zIndex: 9,

                                                border: "1px solid #9F9F9F",
                                                borderRadius: "10px",
                                            }}
                                            type="text"
                                        />
                                   </Form.Group>
                                </div>




                            </div>
                        </div>
                    </Col>
                </Row>
                <div
                    style={{
                        marginTop: "340px",
                        float: "right",
                        display: "flex",
                        justifyContent: "center",
                        flexDirection: "row",
                        gap: "31px",
                    }}
                >
                    <button
                        style={{
                            borderRadius: "25px",
                            width: "159px",
                            height: "52px",
                            backgroundColor: "#FFFFFF",
                            border: "1px  solid #9F9F9F",
                        }}
                        variant="outlined"
                    >
                        Cancel
                    </button>
                    <button
                        style={{
                            borderRadius: "25px",
                            width: "159px",
                            height: "52px",
                            backgroundColor: "#662671",
                            border: "1px  solid #9F9F9F",
                            color: "white",
                        }}
                        variant="outlined"
                    >
                        Save
                    </button>
                </div>
                <div style={{
                    display: 'flex',
                    flexDirection: 'row',
                    gap: '20px',
                    marginTop: '10px'
                }}>




                </div>
            </Container>
        </>
    );
};

export default AddProduct;
